import React, { useState, useContext, useEffect } from 'react'
import { authenticationService } from './services/authentication_service'
import { useHistory } from 'react-router-dom'
import logo from './Logo/Logo.png'
import {
  Container,
  Navbar,
  Nav,
  Button,
  Modal,
  Form,
  Dropdown,
  DropdownButton,
} from 'react-bootstrap'

var managerInfo = {
  id: '1',
  RoleName: 'Manager',
  FullName: 'Andrii Andriovich',
  Email: 'manager@gmail.com',
  PhoneNumber: '095435t436223',
  Password: '3425'
}
var customerInfo = {
  id: '0',
  RoleName: 'User',
  FullName: 'Ivan Ivanovich',
  Email: 'customer@gmail.com',
  PhoneNumber: '0954352462623',
  Password: '5233'
}

export default function Header() {
   
    const [person, setPerson] = useState(null);
    const [show, setShow] = useState(false);
    const [userId, setUserId] = useState(0);
    
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    let history = useHistory();
    const [email, setEmail] = useState("");
    const [password, setPassword]= useState("");

    async function handleSignIn() {
       
        //authenticationService.login("user@example.com", "string");
        await authenticationService.login(email, password);
        var personData = localStorage.getItem("currentUser");
        var data = JSON.parse(personData);
        if (personData !== null) {
            setPerson(data);
        }
        setShow(false);
    }

  function handleSignOut() {
    localStorage.clear()
    setPerson(null)
  }

    function handleProfileClick(){
        history.push({
            pathname: '/user/' + person.id,
            state: { personId: person.id}
        });
    }
    useEffect(()=>{
        var personData = localStorage.getItem("currentUser");
        var data = JSON.parse(personData);
        if (personData !== null) {
            setPerson(data);
        }
    }, []);
    const getDropdown = ()=>{
        if(person !== null){
            return( 
                            <Nav>
                                <DropdownButton id="dropdown-basic-button" title={person.name + ' ' + person.surname}>
                                {/* <Dropdown.Item variant="primary" id="dd-but-sign-in" onClick={handleShow}>Sign in</Dropdown.Item> */}
                                <Dropdown.Item variant="primary" id="dd-but-profile" onClick = {handleProfileClick}>Profile</Dropdown.Item>
                                <Dropdown.Item variant="primary" id="dd-but-sign-out" onClick={handleSignOut}>Sign out</Dropdown.Item>
                            </DropdownButton>
                        </Nav>)
                    }else {
                        return (
                            <Nav>
                            <DropdownButton id="dropdown-basic-button" title="Account">
                                <Dropdown.Item variant="primary" id="dd-but-sign-in" onClick={handleShow}>Sign in</Dropdown.Item>
                            </DropdownButton>
                        </Nav>
            )
        }
    }
    return (
        <Container>
            <Navbar collapseOnSelect expand="md" bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand href="/">
                        <img
                            src = {logo}
                            height="40"
                            width="auto"
                            className="d-inline-block align-top"
                            alt="Logo"
                        /> LandSelling
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="/"> Lots </Nav.Link>
                            <Nav.Link href="/about"> About us </Nav.Link>
                            <Nav.Link href="/rules"> Rules </Nav.Link>
                        </Nav>
                        {getDropdown()}
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Log in</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group controlId="fromBasicEmail">
                            <Form.Label>Email Address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" value ={email} onChange={(e)=>{setEmail(e.target.value)}}/>
                            <Form.Text className="text-muted">We`ll never share your email with anyone else.</Form.Text>
                        </Form.Group>
                        <Form.Group controlId="fromBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Enter password" value ={password} onChange={(e)=>{setPassword(e.target.value)}}/>
                        </Form.Group>
                        <Form.Group controlId="fromBasicCheckbox">
                            <Form.Check type="checkbox" label="Remember me" />
                        </Form.Group>
                        <Form.Group controlId="fromSignInButton">
                            <Button variant="primary" onClick={handleSignIn}>Sign in</Button>
                        </Form.Group>
                    </Form>
                </Modal.Body>
            </Modal>
        </Container>
        );
    }
