import React, { useState, useEffect, useRef } from "react";
import { Container, Col, Row, Button, Form } from "react-bootstrap";
import style from "./NewLot.module.sass";
import saveOrder from "./Services/SaveOrder";
import LoadServiceData from "./Services/DataLoading";
import { useHistory } from "react-router-dom";
import UploadImage from "./Components/UploadImage";
import TextData from "../../Assets/jsonData/NewLotPage.json";
import Spinner from "react-bootstrap/spinner";
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableRow from '@material-ui/core/TableRow'

const columns = [
  {
    id: 'days',
    label: 'Days',
    minWidth: 100,
    align: 'left',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'cost',
    label: 'Cost',
    minWidth: 100,
    align: 'left',
    format: (value) => value.toLocaleString('en-US'),
  },
]

export default function NewLot() {
  let history = useHistory();

  const [data, saveData] = useState({
    isLoading: false,
    requests: null,
    inProgress: null,
  });

  //#region
  //current lot state

  const lotInitialState = {
    fileString: "",
  };

  const [lot, setLot] = useState(lotInitialState);

  function setLotPicture(fileString) {
    setLot((prevState) => ({ ...prevState, fileString }));
  }
  function setLotService(selectedService) {
    setLot((prevState) => ({ ...prevState, selectedService }));
  }
  //end current pruct state
  //#endregion

  const [mylotArray, updateMylotArray] = useState([]);
  const [myCommentArray, updateMyCommentArray] = useState([]);

  const commentTextArea = useRef(null);

  function addCommentHandleInputChange(e) {
    const value = commentTextArea.current.value;
    updateMyCommentArray(value);
  }

  const setCencellot = (lot) => {
    console.log(lot);
    alert("delete lot");
    updateMylotArray(mylotArray.filter((item) => item.id !== lot.id));
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setLot((prev) => ({ ...prev, [name]: value }));
  };

  const addlot = () => {
    updateMylotArray((arr) => [
      ...arr,
      {
        id: "id" + Math.random().toString(16).slice(2),
        picture: lot.fileString.toString(),
        name: lot.selectedService.name,
      },
    ]);
  };

  const newOrder = () => {
    console.log(mylotArray);
    saveOrder(mylotArray, myCommentArray);
    history.push({
      pathname: "/order_list",
    });
  };

  const [newPriceCoef, setNewPriceCoef] = useState({
    days: 1,
    cost: 1,
  });

  const daysClickHandler = (v) => {
    setNewPriceCoef({ days: v, cost: newPriceCoef.cost });
  };
  const costClickHandler = (v) => {
    setNewPriceCoef({ days: newPriceCoef.days, cost: v });
  };

  const [priceCoefs, setPriceCoefs] = useState([]);

  const addNewPriceCoef = () => {
    const PriceCoef = { days: newPriceCoef.days, cost: newPriceCoef.cost };

    setPriceCoefs((prev) => [...prev, PriceCoef]);
  };

  useEffect(() => {
    console.log(priceCoefs);
  }, [priceCoefs]);

  return (
    <Container>
      {data.isLoading ? (
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      ) : (
        <Container>
          <Row className="OrderHeader">
            <div>{TextData.NewOrder}</div>
          </Row>

          <Row className="containerStyle">
            <Col>
              <UploadImage
                fileString={lot.fileString}
                setFileString={(value) => {
                  setLotPicture(value);
                }}
              />
            </Col>

            <Col className="ServicesBox">
              <Container>
                <Row className="HeaderText">
                  <div>{TextData.Services}</div>
                </Row>
                <Row>
                  <TableContainer >
                    <Table stickyHeader aria-label="sticky table">
                      <TableRow>
                        {columns.map((column) => (
                          <TableCell
                            key={column.id}
                            align={column.align}
                            style={{ minWidth: column.minWidth }}
                          >
                            {column.label}
                          </TableCell>
                        ))}
                      </TableRow>

                      <TableBody>
                        {priceCoefs
                          .map((row) => {
                            return (
                              <TableRow
                                hover
                                role="checkbox"
                                tabIndex={-1}
                                key={row.code}
                              >
                                {columns.map((column) => {
                                  const value = row[column.id];
                                  return (
                                    <TableCell
                                      key={column.id}
                                      align={column.align}
                                    >
                                      {column.format &&
                                      typeof value === "number"
                                        ? column.format(value)
                                        : value}
                                    </TableCell>
                                  );
                                })}
                              </TableRow>
                            );
                          })}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Row>
                <Row>
                  <Col>
                    <label>
                      Days
                      <input
                        type="number"
                        min="1"
                        max="100"
                        defaultValue="1"
                        onChange={(e) => daysClickHandler(e.target.value)}
                      />
                    </label>
                  </Col>
                  <Col>
                    <label>
                      Cost
                      <input
                        type="number"
                        min="1"
                        max="100"
                        defaultValue="1"
                        onChange={(e) => costClickHandler(e.target.value)}
                      />
                    </label>
                  </Col>
                  <Col>
                    <Button variant="primary" onClick={addNewPriceCoef}>Add price coef</Button>
                  </Col>
                </Row>
                <Row className="AdditionalTextBlock">
                  <Col className="AdditionalTextL">
                    <div>
                      <label>{TextData.Amount}</label>
                      <input
                        className="numberInputStyle"
                        type="number"
                        id="amount"
                        name="amount"
                        min="1"
                        max="1000"
                        onChange={handleInputChange}
                      />
                    </div>
                  </Col>
                </Row>
              </Container>
            </Col>
          </Row>

          <Row>
            <Col>
              <Button
                className="lotButton"
                variant="primary"
                onClick={newOrder}
              >
                {TextData.Create}
              </Button>
            </Col>
            <Col>
              <Button className="lotButton" variant="warning" onClick={addlot}>
                {TextData.Addlot}
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <div className="containerStyle CommentBlock">
                <label className="HeaderText CommentText">
                  {TextData.OptionalComment}
                </label>

                <textarea
                  className="Input InputAreaStyle"
                  ref={commentTextArea}
                />

                <Button
                  className="lotButtonDown"
                  variant="primary"
                  onClick={addCommentHandleInputChange}
                >
                  {TextData.AddComment}
                </Button>
              </div>
            </Col>

            <Col>
              <div className="lotListStyle">
                <div className="HeaderText CommentText">{TextData.lotList}</div>
              </div>
            </Col>
          </Row>
        </Container>
      )}
    </Container>
  );
}
