import React from 'react'
import { Container, Col, Row } from 'react-bootstrap'
//import { HomeBackground } from './green-fields.jpg'
import './Home.css'
import Button from 'react-bootstrap/Button'
import { useHistory } from 'react-router-dom'

function HomePage() {
  let history = useHistory()

  var personInfo = localStorage.getItem('currentUser')
  var UserData = JSON.parse(personInfo)
  //console.log(UserData)

  const newLandRent = () => {
    history.push({
      pathname: '/user/land/rents' + UserData?.id, // userId must be here
      state: { personId: UserData?.id }, // here too
    })
  }

  const newLandSelling = () => {
    history.push({
      pathname: '/user/land/selling' + UserData?.id, // userId must be here
      state: { personId: UserData?.id }, // here too
    })
  }

  const newBuildingRent = () => {
    history.push({
      pathname: '/user/building/rent' + UserData?.id, // userId must be here
      state: { personId: UserData?.id }, // here too
    })
  }

  const newBuildingSelling = () => {
    history.push({
      pathname: '/user/building/selling' + UserData?.id, // userId must be here
      state: { personId: UserData?.id }, // here too
    })
  }

  const BuildingButton = () => {
    const [showResults, setShowResults] = React.useState(false)
    const onClick = () => setShowResults(true)
    return (
      <div>
        <input type='submit' value='Building' onClick={onClick} />
        {showResults ? <BuildingResults /> : null}
      </div>
    )
  }

  const BuildingResults = () => (
    <Container>
      <Button
        className='BookingButton'
        variant='warning'
        onClick={newBuildingRent}
      >
        Rent
      </Button>

      <Button
        className='BookingButton'
        variant='warning'
        onClick={newBuildingSelling}
      >
        Buy
      </Button>
    </Container>
  )

  const LandButton = () => {
    const [showResults, setShowResults] = React.useState(false)
    const onClick = () => setShowResults(true)
    return (
      <div>
        <input type='submit' value='Land' onClick={onClick} />
        {showResults ? <LandResults /> : null}
      </div>
    )
  }

  const LandResults = () => (
    <Container>
      <Button className='BookingButton' variant='warning' onClick={newLandRent}>
        Rent
      </Button>
      <Button
        className='BookingButton'
        variant='warning'
        onClick={newLandSelling}
      >
        Buy
      </Button>
    </Container>
  )

  return (
    <Container className='PageStyle'>
      <Row>
        <Col>
          <h1>LAND AND BUILDING</h1>
          <h5>Find you best offer for you with our service</h5>
        </Col>
        <Col>
          <BuildingButton />
          <LandButton />
        </Col>
      </Row>
      <Row>
        <h1> Our benefites... ... ... ... ...</h1>
      </Row>
    </Container>
  )
}

export default HomePage
